mapper.select:
	select c.id, c.discount, c.chk, m.bis_name
	  from coupon_history c, member m 
	 
	 	
	  		where c.id = #{id}
	  	
	  	
	  	
			where m.id = #{id}
		
	 
	 
	 
	 	and c.discount = #{discount}
	 
	 and c.bis_no=m.bis_no
   order by c.his_no desc limit ${startrow}, 10;

select count(*)
  from trade
 
 	
  		where buy_id = #{id}
  	
  	
  	
		where sell_id = #{id}

select count(*)
  from trade t,product p 
 
 	
  		where t.buy_id = #{id}
  	
  	
  	
		where t.sell_id = #{id}	
	
 
  and t.pro_no=p.pro_no

select count(*)
  from coupon_history c, member m 
 
 	
  		where c.id = #{id}
  	
  	
  	
		where m.id = #{id}
	
 
 
 
 	and c.discount = #{discount}
 
 and c.bis_no=m.bis_no
